import React, {Component} from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { firestoreConnect } from 'react-redux-firebase';
import { compose } from 'redux'; 
import { Redirect } from 'react-router-dom';
import moment from 'moment';
import { deleteProject} from '../../store/actions/projectActions';



class ProjectDetails extends Component {
    componentWillMount() {
        this.props.getProjects();
      }

  

  render() {
    const { project, auth } = this.props;
    if (!auth.uid) return <Redirect to="/signin" />;
    if (project) {
      return (
        <div className="container section project-details">
        <div className="card z-depth-0" >
            <div className="card-content">
                <span className="card-title">Start Time: {project.startTime }</span>
                <span className="card-title">End Time: {project.endTime }</span>
            </div>
            <div className="card-action grey lighten-4 grey-text">
                <div>Posted by {project.authorFirstName} {project.authorLastName} </div>
                <div>{moment(project.createdAt.toDate().toString()).calendar()}</div>
            </div>
            <button className="btn btn-danger float-right" onClick={() => this.props.deleteProject(project)}>Delete</button>
            </div>
            </div>
            );
          } else {
            return (
              <div className="container center">
              <p>Loading timesheet...</p>
          </div>
            );
          }
        }
      }
      
      const mapStateToProps = (state, ownProps) => {
        const id = ownProps.match.params.id;
        const projects = state.firestore.data.projects;
        const project = projects ? projects[id] : null;
        return {
          project: project,
          auth: state.firebase.auth,
          projectId: id
        };
      };
      
      export default compose(
        connect(
          mapStateToProps,
          { deleteProject}
        ),
        firestoreConnect([{ collection: "timesheets" }])
      )(ProjectDetails);



