import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';

import { createProject } from '../../store/actions/projectActions';


class CreateProject extends Component {
    state = {
        startTime: '',
        endTime: ''
    }

    handleChange = (e) => {
        this.setState({
        [e.target.id]: e.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        // console.log(this.state);
        this.props.createProject(this.state)
        this.props.history.push('/');
    }

    onEditProject = (project, text) => {
        const { uid, ...projectSnapshot } = project;
    
        this.props.firebase.project(project.uid).set({
          ...projectSnapshot,
          text,
          editedAt: this.props.firebase.serverValue.TIMESTAMP,
        });
      };
    
      onRemoveProject = uid => {
        this.props.firebase.project(uid).remove();
      };

    render() {
        const { auth } = this.props;
        if (!auth.uid) return <Redirect to='/signin' />
        return (
            <div className="container">
                <form onSubmit={this.handleSubmit} className="white">
                    <h5 className="grey-text text-darken-3">Create/Update Timesheet</h5>
                    <div className="input-field">
                        <label htmlFor="startTime">Start Time</label>
                        <input type="text" id="startTime" onChange={this.handleChange} />
                    </div>
                    <div className="input-field">
                        <label htmlFor="endTime">End Time</label>
                        <input type="text" id="endTime" onChange={this.handleChange} />
                    </div>
                    <div className="input-field">
                        <button className="btn pink lighten-1 z-depth-0">Submit</button>
                    </div>
                </form>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        auth: state.firebase.auth
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        createProject: (project) => dispatch(createProject(project))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CreateProject);
