import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';

import { createProject } from '../../store/actions/projectActions';

class editProject extends Component {
    state = {
        startTime: startTime.current.value,
        endTime: startTime.current.value
    }

    handleChange = (e) => {
        this.setState({
        [e.target.id]: e.target.value
        })
    }

    updatePost = (e) => {
        e.preventDefault();
        // console.log(this.state);
        this.props.createProject(this.state)
        this.props.history.push('/project/' + project.id);
    }
    render() {
        const { auth } = this.props;
        if (!auth.uid) return <Redirect to={'project/:id'} />
        return (
            <div className="container">
            <form onSubmit={this.updatePost} className="white">
                <h5 className="grey-text text-darken-3">Update Timesheet</h5>
                <div className="input-field">
                    <label htmlFor="startTime">Start Time</label>
                    <input type="text" id="startTime" defaultValue={project.startTime} />
                </div>
                <div className="input-field">
                    <label htmlFor="endTime">End Time</label>
                    <input type="text" id="endTime" defaultValue={project.startTime}/>
                </div>
                <div className="input-field">
                    <button className="btn pink lighten-1 z-depth-0">Update</button>
                </div>
            </form>
        </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        auth: state.firebase.auth
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        createProject: (project) => dispatch(createProject(project))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(editProject);
