import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';
import firebase from '../../config/fbConfig';

import { createProject } from '../../store/actions/projectActions';

class editProject extends Component {
    state = {
        startTime: '',
        endTime: ''
    }

    updatePost = (e) => {
        e.preventDefault();
        // console.log(this.state);
        this.props.createProject(this.state)
        this.props.history.push('/');
    }
    render(project) {
        const { auth } = this.props;
        if (!auth.uid) return <Redirect to={'project/:id'} />
        return (
            <div className="container">
            <form onSubmit={this.updatePost} key={project.id}>
                <h5 className="grey-text text-darken-3">Update Timesheet</h5>
                <div className="input-field">
                    <label htmlFor="startTime">Start Time</label>
                    <input type="text" id="startTime" defaultValue={firebase.project.startTime} />
                </div>
                <div className="input-field">
                    <label htmlFor="endTime">End Time</label>
                    <input type="text" id="endTime" defaultValue={firebase.project.endTime}/>
                </div>
                <div className="input-field">
                    <button className="btn pink lighten-1 z-depth-0">Update</button>
                </div>
            </form>
        </div>
        )
    }
}

const mapStateToProps = (state, ownProps) => {
    // console.log(state);
    const id = ownProps.match.params.id;
    const projects = state.firestore.data;
    const project = projects ? projects[id] : null
    return {
        project: project,
        auth: state.firebase.auth
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        createProject: (project) => dispatch(createProject(project))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(editProject);
